/*shortkeys.js|WTFPLv2*/$.ctrl=function(a,b,c){$(document).keydown(function(d){if(!c)c=[];if(d.keyCode==a.charCodeAt(0)&&d.ctrlKey){b.apply(this,c);return false}})};$.alt=function(a,b,c){$(document).keydown(function(d){if(!c)c=[];if(d.keyCode==a.charCodeAt(0)&&d.altKey){b.apply(this,c);return false}})};$.shift=function(a,b,c){$(document).keydown(function(d){if(!c)c=[];if(d.keyCode==a.charCodeAt(0)&&d.shiftKey){b.apply(this,c);return false}})}